let pokemonRepository=function(){let t=[];return{add:function(e){t.push(e)},getAll:function(){return t},addListItem:function(t){let e=document.querySelector(".pokemon-list"),n=document.createElement("button");n.classList.add("list-group-item","list-group-item-action"),n.setAttribute("data-toggle","modal"),n.setAttribute("data-target","#pokemonModal"),pokemonRepository.loadListDetails(t).then(function(){let o=document.createElement("span");o.innerText=`No.${t.id.toString().padStart(3,"0")}`,o.classList.add("list-number");let i=document.createElement("img");i.src=`${t.iconUrl}`,i.classList.add("list-icon");let r=document.createElement("div");r.innerText=`${t.name}`,r.classList.add("list-name"),n.appendChild(o),n.appendChild(i),n.appendChild(r),e.appendChild(n)}),n.addEventListener("click",function(){pokemonRepository.showDetails(t)})},loadList:function(){return fetch("https://pokeapi.co/api/v2/pokemon/?limit=150").then(function(t){return t.json()}).then(function(t){t.results.forEach(function(t){let e={name:t.name,detailsUrl:t.url};pokemonRepository.add(e)})}).catch(function(t){console.error(t)})},loadListDetails:function(t){return fetch(t.detailsUrl).then(function(t){return t.json()}).then(function(e){t.iconUrl=e.sprites.versions["generation-viii"].icons.front_default,t.id=e.id}).catch(function(t){console.error(t)})},loadDetails:function(t){return fetch(t.detailsUrl).then(function(t){return t.json()}).then(function(e){t.imageUrl=e.sprites.front_default,t.height=e.height/10,t.types=e.types.map(function(t){return t.type.name})}).catch(function(t){console.error(t)})},showDetails:function(t){pokemonRepository.loadDetails(t).then(function(){document.querySelector(".modal-title").innerText=t.name;document.getElementById("pokemonImage").src=t.imageUrl;document.getElementById("pokemonHeight").innerText=`Height: ${t.height}m`;let e=document.getElementById("pokemonType");if(t.types.length>1){let n=t.types.map(function(t){return`<span class='typing' style="background-color: var(--${t}-color)">${t}</span>`});e.innerHTML=`Types: ${n.join(", ")}`}else e.innerHTML=`Type: <span class='typing' style="background-color: var(--${t.types[0]}-color)">${t.types[0]}</span>`})}}}();window.addEventListener("DOMContentLoaded",function(){pokemonRepository.loadList().then(function(){pokemonRepository.getAll().forEach(function(t){pokemonRepository.addListItem(t)})})});